			if (L & 1) ret += data[L++];
			if (R & 1) ret += data[--R];
			L >>= 1; R >>= 1;
		}
		return ret;
	}
};

int N;
int P[303030];

int odds[303030], evens[303030];

void add_range(int *a, int lo, int hi)
{
	if (lo == hi) return;
	if (lo > hi) swap(lo, hi);
	a[lo] += 1;
	a[hi + 1] -= 1;
}

segtree seg;

bool check(int x)
{
	seg.clear();
	i64 inv = 0, mv = 0;
	for (int i = 0; i < N; ++i) if (i % 2 == x) {
		inv += seg.query(P[i] + 1, N);
		seg.incl(P[i]);
		mv += abs(i - P[i]) / 2;
	}
	return inv * 2 == mv;
}

i64 inv()
{
	i64 ret = 0;
	for (int i = 0; i < N; ++i) {
		ret += seg.query(P[i] + 1, N);
		seg.incl(P[i]);
	}
	return ret;
}

int main()
{
	i64 v, mov;
	scanf("%d", &N);
	for (int i = 0; i < N; ++i) {
		scanf("%d", P + i);
		--P[i];
	}
	for (int i = 0; i < N; ++i) {
		if (i % 2 != P[i] % 2) goto fail;
		if (i % 2 == 0) {
			add_range(evens, i, P[i]);
		} else {
			add_range(odds, i, P[i]);
		}
	}
	for (int i = 1; i < N; ++i) {
		odds[i] += odds[i - 1];
		evens[i] += evens[i - 1];
	}
	for (int i = 0; i < N; ++i) {
		if (odds[i] > 0 && evens[i] > 0) goto fail;
	}

	if (!check(0) || !check(1)) goto fail;

	puts("Yes");
	return 0;
fail:
	puts("No");
	return 0;
}

