int n, cum[MX];

bool OK(vi p) {
    if (sz(p) % 2 != 1) return 0;
    for (int& i: p) if (i < 0 || i >= sz(p)) return 0;
    vi use(sz(p));
    queue<int> todo; 
    for (int i = 1; i < sz(p); i += 2) {
        if (p[i] != i) return 0;
        todo.push(i);
    }
    while (sz(todo)) {
        int x = todo.front(); todo.pop();
    	// cout << "HI " << x << "\n";
        if (!(p[x-1] > x && p[x+1] < x)) continue;
        if (use[x]) return 0;
        swap(p[x-1],p[x+1]); use[x] = 1;
        if (x-2 >= 0) todo.push(x-2);
        if (x+2 < sz(todo)) todo.push(x+2);
    }
    F0R(i,sz(p)) if (p[i] != i) return 0;
    return 1;
}

bool ok(vi p) {
    for (int& i: p) i --;
    F0R(i,sz(p)) {
        int l = i, r = p[i]; if (l > r) swap(l,r);
        cum[l] ++; cum[r] --;
    }
    FOR(i,1,sz(p)) cum[i] += cum[i-1];
    F0R(i,sz(p)) {
        int I = i;
        while (cum[i]) i ++;
        vi tmp;
        FOR(j,I,i+1) tmp.pb(p[j]-I);
        if (!OK(tmp)) return 0;
    }
    return 1;
}

