			return 0;
		}
		switch(i%2){
			case 0:topc++;c[topc]=a[i];break;
			case 1:topb++;b[topb]=a[i];break;
		}
	}
	merge_sort(1,n);
	if(ans%3!=0){
		printf("No\n");
		return 0;
	}
	ans/=3;tmp=ans;
	ans=0;
	for(register int i=1;i<=topb;i++){
		a[i]=b[i];
	}
	merge_sort(1,topb);
	tmp-=ans;
	ans=0;
	for(register int i=1;i<=topc;i++){
		a[i]=c[i];
	}
	merge_sort(1,topc);
	tmp-=ans;
	switch(tmp){
		case 0:printf("Yes\n");break;
		default:printf("No\n");break;
	}
	return 0;
}

