#include <bits/stdc++.h>

template <class T = long long int>
class Bit {
    private:
    T len;
    T *arr;

    public:
    Bit(T l, T initialValue = 0) : len(l) {
        arr = new T[l+1];
        for (T i = 0; i < l + 1; i++) {
            arr[i] = initialValue;
        }
    }
    ~Bit() {
        delete[] arr;
    }

    void update(T a, T newval) {
        for (T x = a; x <= len; x += x & -x) arr[x] += newval;
    }
    T query(T a) const {
        T ret = 0;
        for (T x = a; x > 0; x -= x & -x) ret += arr[x];
        return ret;
    }
};

int main() {
    int n;
    std::cin >> n;
    std::vector<int> p(n + 1);
    std::deque<std::deque<int>> qs {{}};
    int prev = 0;
    for (int i = 1; i <= n; i++) {
        std::cin >> p[i];
        if ((p[i] - i) % 2) {
            std::cout << "No" << std::endl;
            return 0;
        }
        if (p[i] == i) {
            if (i - prev == 1) {
                if (qs.back().size()) qs.back().push_back(p[i]);
                else prev = i;
            } else {
                prev = i;
            }
        } else {
            if (i - prev == 1) qs.back().push_back(p[i]);
            else if (i - prev == 2) qs.push_back({p[i]});
            else {
                std::cout << "No" << std::endl;
                return 0;
            }
        }
    }

    for (auto& q : qs) {
        if (!q.size()) continue;
        // for (auto el : q) std::cout << el << " ";
        std::cout << std::endl;

        Bit<int> b(1000000);
        int inv = 0;
        for (auto el : q) {
            inv += b.query(el);
            b.update(el, 1);
        }
        inv = q.size() * (q.size() - 1) / 2 - inv;
        // std::cout << "inv = " << inv << std::endl;
        if (inv > q.size() - 1) {
            std::cout << "No" << std::endl;
            return 0;
        }
    }

    std::cout << "Yes" << std::endl;

    return 0;
}

