            puts(no);
            return 0;
          }
          lastLeft = p[j];
        }
        else {
          if (lastRight > p[j]) {
            puts(no);
            return 0;
          }
          lastRight = p[j];
        }
      }

      i = j;
    }
  }

  puts("Yes");
  return 0;
