int N;
vector<int> P, invP;
vector<int> le, ri;
vector<int> dir; // -1: 左、0: 不動、1: 右

bool subSolve(int left, int right) {
    // はみ出したらだめ
    for (int i = left; i < right; ++i) {
        if (invP[i] < left || invP[i] >= right) return false;
    }
    return true;
}

bool solve() {
    // それぞれ初期状態から左右にどちらに動いたか
    dir.resize(N);
    for (int i = 0; i < N; ++i) {
        if (P[i] == i) {
            dir[P[i]] = 0;
        }
        else if (P[i] < i) {
            dir[P[i]] = 1;
            ri.push_back(P[i]);
        }
        else {
            dir[P[i]] = -1;
            le.push_back(P[i]);
        }

        if (abs(P[i] - i) % 2 != 0) return false; // 移動量は偶数じゃないとダメ
    }

    //COUT(invP); COUT(le); COUT(ri); COUT(dir);


    // 区間分割して区間ごとに解く
    for (int i = 0; i < N; ++i) {
        if (dir[i] == 1) {
            int j = i;
            for (j = i; j < N; j += 2) {
                if (dir[j] == 1) {
                    if (j+1 >= N || dir[j+1] != 0) return false;
                    if (j+2 >= N || dir[j+2] == 0) return false;
                }
                else if (dir[j] == -1) {
                    if (j+1 >= N) break;
                    if (dir[j+1] == -1) return false;
                    else if (dir[j+1] == 1) break;
                    else if (dir[j+1] == 0) {
                        if (j+2 >= N) break;
                        if (dir[j+2] == 0) break;
                        if (dir[j+2] == 1) break;
                    }
                }
            }
            //COUT(make_pair(i, j+1));
            if (!subSolve(i, j+1)) return false; // 区間
            i = j;
        }
        else if (dir[i] == -1) return false; // 原理的にないはず
    }

    // 順序が入れ替わってるかどうか
    for (int i = 0; i+1 < le.size(); ++i) {
        if (le[i] > le[i+1]) return false;
    }
    for (int i = 0; i+1 < ri.size(); ++i) {
        if (ri[i] > ri[i+1]) return false;
    }

    return true;
}

int main() {
    cin >> N;
    P.resize(N); invP.resize(N);
    for (int i = 0; i < N; ++i) cin >> P[i], --P[i], invP[P[i]] = i;
    if (solve()) cout << "Yes" << endl;
    else cout << "No" << endl;
}

