}
ll qry2(int l, int r) {
    ll retl = 0, retr = 0;
    for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
        if (l & 1) retl = max(retl, t2[l++]);
        if (r & 1) retr = max(retr, t2[--r]);
    }
    return max(retl, retr);
}
bool ispos(int l, int r) {
    if ((r - l) % 2 == 0 || p[l] != r) return 0;
    for (int i = l; i + 2 < r; i += 2) {
        if (!(p[i + 1] > p[i + 2])) return 0;
        swap(p[i], p[i + 2]);
    }
    for (int i = l; i < r; i++) {
        if (p[i] != i + 1) return 0;
    }
    return 1;
}

