						goto END;
					}
				}
				for(int32_t j=head;j<original_position-1;j++){
					if(p[j]!=j){
						possible=false;
						goto END;
					}
				}
				head=original_position-1;
			}else{
				possible=false;
				break;
			}
		}else{
			head++;
		}

	}
	END:
	if(possible){
