int n, p[N], a[N], mi[N];

bool check(int u) {
    if (1 < u && u < n && p[u - 1] > p[u] && p[u] > p[u + 1])
        return swap(p[u - 1], p[u + 1]), true;
    else return false;
}

int stk[N], top;

int main() {
#ifdef isLOCAL
