    freopen("work.out", "w", stdout);
#endif

    cin >> n;
    for (int i = 1; i <= n; ++i)
        scanf("%d", p + i);

    p[n + 1] = n + 1, p[0] = 0;
    for (int i = 1; i <= n; ++i) {
        if (p[i - 1] != i - 1 && p[i] != i && p[i + 1] != i + 1)
            return puts("No"), 0;
        a[i] = i == p[i];
    }


    for (int i = 1, j; i <= n; i = j + 1) {
        for (j = i; j < n && a[j] != a[j + 1]; ++j);
        top = 0;
        for (int k = i; k <= j; ++k) {
            if (p[k] < i || j < p[k]) return puts("No"), 0;
            if (!a[k]) stk[++top] = p[k];
        }
        mi[top] = stk[top];
        for (int k = top - 1; k; --k)
            mi[k] = min(mi[k + 1], stk[k]);
        int mx = stk[1];
        for (int k = 2; k < top; ++k) {
            mx = max(mx, stk[k]);
            if (mi[k] < stk[k] && stk[k] < mx)
                return puts("No"), 0;
        }
    }

    return puts("Yes"), 0;

    queue<int> q;
    for (int i = 2; i < n; ++i)
        if (check(i)) q.push(i);

    while (!q.empty()) {
        int u = q.front(); q.pop();
        if (check(u - 2)) q.push(u - 2);
        if (check(u + 2)) q.push(u + 2);
    }

    for (int i = 1; i <= n; ++i)
        if (p[i] != i) return puts("No"), 0;
    puts("Yes");

    return 0;
}

#include <bits/stdc++.h>
using namespace std;
void check(vector<int> &v, int st, int fin){
    vector<int> good;
    set<int> unused;
    for (int i=st; i <= fin; i++) unused.insert(v[i]);
    int ind = fin;
    while (ind >= st){
        set<int>::iterator it = unused.end();
        it--;
        int num = *it;
        while (true){
            unused.erase(unused.find(v[ind]));
            if (v[ind] == num){
                good.push_back(ind);
                ind--;
                break;
            }
            ind--;
        }
    }
    reverse(good.begin(), good.end());
    int u = 0;
    for (int i=st; i <= fin; i++){
        if (u < good.size() && good[u] == i){
            u++;
            continue;
        }
        int L=i, R = i;
        int diff = i - st;
        if (diff%2 == 0) L -= 2*u;
        L++, R++;
        //cout << i << " " << pl << endl;
        if (L <= v[i] && R >= v[i]) continue;
        cout << "No";
        exit(0);
    }
}
void solve(vector<int> &v, int st){
    if (st == v.size()) return;
    set<int> ms;
    for (int i=st; i < v.size(); i++){
        ms.insert(v[i]-st);
        set<int>::iterator it = ms.end();
        it--;
        int mx = *(it);
        if (mx == ms.size()){
            check(v, st, i);
            solve(v, i+1);
            return;
        }
    }
}
signed main() {
    int n;
    cin >> n;
    vector<int> v(n);
    for (int i=0; i < n; i++) cin >> v[i];
    solve(v, 0);
    cout << "Yes";

}

