 bool fun(int a, int b)
{
	if ((b - a) % 2) return false;
	for (int i = a + 1; i < b; i += 2) {
		if (p[i] != i) return false;
	}
	while (!dq.empty()) dq.pop_back();
	while (!stk.empty()) stk.pop();
	dq.push_back(p[a]);
	bool rv = true;
	for (int i = a + 2; i <= b; i += 2) {
