#include <iostream>
#include <vector>
using namespace std;

template<typename T> long long inversion_number_aux(vector<T> &v, int l, int r, vector<T> &tmp) {
	if (r - l <= 1) return 0;
	long long res = 0;
	const int half = (l + r) / 2;
	res += inversion_number_aux(v, l, half, tmp);
	res += inversion_number_aux(v, half, r, tmp);
	for (int i = l, j = half, k = l; k < r; k++) {
		bool right;
		if (i == half) right = true;
		else if (j == r) right = false;
		else right = v[j] < v[i];

		if (right) tmp[k] = v[j++];
		else {
			tmp[k] = v[i++];
			res += j - half;
		}
	}
	for (int i = l; i < r; i++) {
		v[i] = tmp[i];
	}
	return res;
}

template<typename T> long long inversion_number(vector<T> v) {
	vector<T> tmp(v.size());
	return inversion_number_aux<T>(v, 0, v.size(), tmp);
}

int main() {
	int n;
	cin >> n;
	vector<int> p(n), p0, p1;
	for (int i = 0; i < n; i++) {
		scanf("%d", &p[i]);
		p[i]--;
		if (p[i] % 2 != i % 2) {
			cout << "No" << endl;
			return 0;
		}
		if (i % 2 == 0) p0.push_back(p[i]);
		else p1.push_back(p[i]);
	}
	long long inv = inversion_number(p);
	if (inv % 3 != 0 || inv / 3 != inversion_number(p0) + inversion_number(p1)) {
		cout << "No" << endl;
	} else cout << "Yes" << endl;
	return 0;
}

