bool check (int l, int r)
{
    //cout << l << " " << r << "\n";

    int mlo = MAXN, mhi = 0;
    for (int i = l; i <= r; i++)
    {
        mlo = min (mlo, arr[i]);
        mhi = max (mhi, arr[i]);
    }
    if (mlo != l || mhi != r)
        return false;
    set <int> s;

    int cm = -1;
    for (int i = l; i <= r; i += 2)
        s.insert(i);
    for (int i = l; i <= r; i += 2)
    {
        if (cm > arr[i])
        {
            if (*s.begin() < arr[i])
                return false;
        }
        s.erase(arr[i]);
        cm = max (cm, arr[i]);
    }
    return true;
}

