using namespace std;

int n,seq[300050];
bool flg[300050];

bool chk(int l,int r)
{
	if(l > r) return 0;
	if(l == r) return seq[l] != l;
	int minn = 300001,maxn = 0;
	for(int i = l;i <= r; ++ i) minn = min(minn,seq[i]),maxn = max(maxn,seq[i]);
	if(minn != l || maxn != r) return 1;
	for(int i = l + 1;i <= r;i += 2)
		if(!flg[i])
			return 1;
	for(int i = l;i <= r;i += 2)
		if(flg[i])
			return 1;
	int lst1 = 0,lst2 = 0;
	for(int i = l;i <= r;i += 2)
		if(seq[i] > i)
		{
			if(lst1 > seq[i])
				return 1;
			lst1 = seq[i];
		}
		else
		{
			if(lst2 > seq[i])
				return 1;
			lst2 = seq[i];
		}
	return 0;
}

int main()
{
	scanf("%d",&n);
	for(int i = 1;i <= n; ++ i)
	{
		scanf("%d",&seq[i]);
		flg[i] = seq[i] == i;
	}
	flg[0] = 1;
	int las = 1;
	for(int i = 1;i <= n; ++ i)
	{
		if(flg[i] && flg[i - 1])
		{
			if(chk(las,i - 2))
			{
				printf("No\n");
				return 0;
			}
			las = i + 1;
		}
		else if(flg[i] && flg[i - 2]);
		else if(flg[i] && flg[i - 3])
		{
			if(chk(las,i - 2))
			{
				printf("No\n");
				return 0;
			}
			las = i - 1;
		}
		else if(flg[i])
		{
			printf("No\n");
			return 0;
		}
	}
	if(chk(las,n)) printf("No\n");
	else printf("Yes\n");
}

