int main() {
  std::cin.tie(0); std::cin.sync_with_stdio(false);
  int n; std::cin >> n;
  std::vector< int > a(n); std::cin >> a;
  for (auto & x : a) x--;
  a.emplace_back(n);
  std::set< int > set;
  auto cal = [&] (auto l, auto r) {
    require((r - l) % 2 == 0);
    for (auto i = l + 1; i < r; i += 2)
      { require(a.at(i) == i); }
    auto L = std::find(a.begin() + l, a.begin() + r, r) - a.begin();
    auto R = std::find(a.begin() + l, a.begin() + r, l) - a.begin();
    require(R - L == 2);
    require((L - l) % 2 == 0);
    for (auto i = l; i < L; i += 2)
      { require(a.at(i) == i + 2); }
    for (auto i = R + 2; i < r; i += 2)
      { require(a.at(i) == i - 2); }
  };
  auto l = -1;
  rep(i, 0, n + 1) {
    auto crr = a.at(i);

    if (set.empty()) {
      require(i <= crr);
      if (i < crr) {
        l = i;
        set.insert(crr);
      }
    }

    else {
      assert(0 <= l);
      require(l <= crr);
      if (i < crr) {
        set.insert(crr);
      }
      assert(i <= *set.begin());
      if (i == *set.begin()) {
        set.erase(set.begin());
        if (set.empty()) {
          cal(l, i);
        }
      }
    }
  }
  std::cout << "Yes" << std::endl;
  return 0;
}

