const int MAX_N = 3e5 + 100;

int N, Nr[MAX_N];
int Cnt[MAX_N];
bool isCanInterval(int s, int e) {
	for(int i=s; i<=e; i++) Cnt[i]++;
	bool isAllOne = true;
	for(int i=s; i<=e; i++) if(Cnt[i] != 1) isAllOne = false;
	if(not isAllOne) return false;
	vector<int> list[2];
	for(int i=s; i<=e; i+=2) {
		if(Nr[i] < i) list[0].push_back(Nr[i]);
		else list[1].push_back(Nr[i]);
	}
	bool isIncrease = true;
	for(int k=0; k<2; k++) {
		for(int i=0; i+1<SZ(list[k]); i++) {
			isIncrease &= (list[k][i] < list[k][i+1]);
		}
	}
	return isIncrease;
}
bool isCan() {
	for(int s=1, e=1; s<=N; s=e+1) {
		if (Nr[s] == s) {
			e=s;
			continue;
		}
		e=s;
		while(e+2 <= N && Nr[e+1] == e+1 && Nr[e+2] != e+2) e+=2;
		if(e == s) return false;
		if(not isCanInterval(s, e)) return false;
	}
	return true;
}
int main() {
	cin >> N;
	for(int i=1; i<=N; i++) scanf("%d", &Nr[i]);
	if(isCan()) puts("Yes"); else puts("No");
	return 0;
}

