bool mysort(int l, int r){
	vector<int> hoge;
	bool res = false;
	int k = 0;

	for(int i = l;i < r;i+=2){
		if(!hoge.empty() && hoge.back() > p[i])res = true; 
		hoge.push_back(p[i]);
	}
	sort(hoge.begin(), hoge.end());
	k = 0;
	for(int i = l;i < r;i+=2){
		p[i] = hoge[k++];
	}
	hoge.clear();

	for(int i = l+1;i < r;i+=2){
		if(!hoge.empty() && hoge.back() > p[i])res = true; 
		hoge.push_back(p[i]);
	}
	sort(hoge.begin(), hoge.end());
	k = 0;
	for(int i = l+1;i < r;i+=2){
		p[i] = hoge[k++];
	}
	return res;
}

int main(){
