// 1-indexed BIT (i : [1, len])
template<typename T, int len>
struct BIT
{
    array<T, len + 1> val;
    void reset() { val.fill(0); }
    BIT() { reset(); }
    void add(int pos, T v)
    {
        while (pos > 0 && pos <= len)
        {
            val[pos] += v;
            pos += pos & -pos;
        }
    }
    T sum(int pos)
    {
        T res = 0;
        while (pos > 0)
        {
            res += val[pos];
            pos -= pos & -pos;
        }
        return res;
    }
};
BIT<int, 300001> bit;
lint count_inversion(const vector<int> &vec)
{
    bit.reset();
    lint ans = 0;
    for (auto v : vec)
    {
        ans += bit.sum(v);
        bit.add(v + 1, 1);
    }
    return (lint)vec.size() * (vec.size() - 1) / 2 - ans;
}

