bool rev(int p) {
	if (a[p] > a[p + 1] && a[p + 1] > a[p + 2]) {
		swap(a[p + 2], a[p]);
		return true;
	} else return false;
}

bool work() {
	int p = 0;
	for (int i = n; i; --i)
		if (a[i] == n) { p = i; break; }
	if (n && p == n) return --n, true;
	if ((n - p) & 1) return false;
	for (int i = p; a[n] != n; i += 2) if (!rev(i)) return false;
	while (n && a[n] == n) --n;
	if (n > p + 4) return false;
	return true;
}

