int N;
int A[300001];
int tmp[300001];

int cnt_bubble(int b,int e){
  if(b==e)return 0;
  if(b+2==e){
    if(A[b]>A[e]){
      swap(A[b],A[e]);
      return 1;
    }
    return 0;
  }
  int mid=(b+e)/2;
  if((mid-e)%2==1)mid--;
  int ret=0;
  int l=cnt_bubble(b,mid);
  int r=cnt_bubble(mid+2,e);
  ret=l+r;
  int ln=(mid-b)/2+1,rn=(e-mid)/2;
  int nn=ln+rn;
  int i=0;
  int bb=b,mm=mid+2;
  int llnn=ln;
  while(i<nn){
    if(bb>mid){
      tmp[i]=A[mm];
      i++;
      mm+=2;
    }else if(mm>e){
      tmp[i]=A[bb];
      i++;
      bb+=2;
    }else if(A[bb]>A[mm]){
      tmp[i]=A[mm];
      cerr<<"tmp["<<i<<"]=A["<<mm<<"]"<<endl;
      ret+=llnn;
      mm+=2;
      i++;
    }else if(A[bb]<A[mm]){
      tmp[i]=A[bb];
      cerr<<"tmp["<<i<<"]=A["<<bb<<"]"<<endl;
      llnn--;
      bb+=2;
      i++;
    }
  }
  For(ii,nn){
    if(b+(ii*2)>N){exit(0);}
    A[b+(ii*2)]=tmp[ii];
  }
  cerr<<"tmp:";
  For(ii,nn){
    cerr<<tmp[ii]<<" ";
  }
  cerr<<endl;
  return ret;
}

int main(){
  cin>>N;
  REP(N){
    cin>>A[i];
    A[i]--;
    if((A[i]%2)!=(i%2)){
      cout<<"No"<<endl;
      return 0;
    }
  }
  int i=0;
  while(i<N){
    int b=i,e=A[i];
    int j=i;
    while(true){
      e=max(e,A[j]);
      if(j>e)goto NO;
      if(j==e)break;
      j++;
    }
    if((e-b)%2==1)
      goto NO;
    if(b==e){
      i=e+1;
      continue;
    }
    int cnt=0;
    for (int j = b+1; j < e; j+=2) {
      if(A[j]!=j)
        goto NO;
      if(A[j-1]>A[j]&&A[j]>A[j+1])cnt++;
    }
    For(ii,e-b+1){
      cerr<<A[ii]<<" ";
    }
    cerr<<endl;
    if(cnt!=1)goto NO;
    cnt=cnt_bubble(b,e);
    cerr<<"("<<b<<","<<e<<")"<<cnt<<":";
    For(ii,e-b+1){
      cerr<<A[ii]<<" ";
    }
    cerr<<endl;
    if(cnt!=(e-b)/2)
      goto NO;
    i=e+1;
  }
  cout<<"Yes"<<endl;
  return 0;
NO:
  cout<<"No"<<endl;
  return 0;
}

