const int N = 4e5 + 10;
int tr[N], a[N], n;
LL a1, a2, a3;

void Modify(int x, int y) {
	for (; x <= n; x += x & -x) tr[x] += y;
}

int ask(int x) {
	int res = 0;
	for (; x; x -= x & -x) res += tr[x];
	return res;
}

int main() {
	n = read();
	for (int i = 1; i <= n; ++ i) {
		a[i] = read();
		if ((a[i] & 1) != (i & 1)) return puts("No"), 0;
		a1 += ask(n) - ask(a[i] + 1);
		Modify(a[i], 1);
	}
	if (a1 % 3) return puts("No"), 0;
	memset(tr, 0, sizeof(tr));
	for (int i = 1; i <= n; i += 2) {
		a2 += ask(n) - ask(a[i] + 1);
		Modify(a[i], 1);
	}
	memset(tr, 0, sizeof(tr));
	for (int i = 1; i <= n; i += 2) {
		a3 += ask(n) - ask(a[i] + 1);
		Modify(a[i], 1);
	}
	if ((a2 + a3) * 3 != a1) return puts("No"), 0;
	puts("Yes");
	return 0;
}

#include <bits/stdc++.h>

using namespace std;

const int MAX_N = 3e5 + 10;

void No() { printf("No\n"); exit(0); }
void Yes() { printf("Yes\n"); exit(0); }

int N, P[MAX_N];
bool diff[MAX_N];

int main() {
    scanf("%d", &N);
    for (int i = 1; i <= N; i++) scanf("%d", &P[i]);
    for (int i = 1; i <= N; i++) diff[i] = P[i] != i;
    int curr = N;
    while (curr) {
        if (!diff[curr]) {
            curr--;
            continue;
        }
        int fir = curr;
        while (fir - 2 > 0 && diff[fir - 2]) {
            fir -= 2;
        }
        for (int i = curr - 1; i >= fir; i -= 2)
            if (diff[i])
                No();

        for (int i = fir; i <= curr; i++) {
            if (P[i] < fir || P[i] > curr)
                No();
        }

        int last = 0;

        for (int i = fir; i <= curr; i += 2) {
            if (P[i] < i) continue;
            if (P[i] < last)
                No();
            last = P[i];
        }

        last = 0;

        for (int i = fir; i <= curr; i += 2) {
            if (P[i] > i) continue;
            if (P[i] < last)
                No();
            last = P[i];
        }

        curr = fir - 1;
    }
    Yes();
}

