int main(){
	cin>>n;
	for(int i=1;i<=n;i++){
		cin>>p[i];
	}
	for(int i=1;i<=n;i+=2){
		if(~p[i]&1){
			puts("No");
			return 0;
		}
	}
	for(int i=2;i<=n;i+=2){
		if(p[i]&1){
			puts("No");
			return 0;
		}
	}
	vector<int>l,r;
	int tot=0,lst;
	for(int i=1;i<=n;i++){
		if(p[i]==i){
			tot++;continue;
		}
		if(tot>=2){
			check(l,r,lst,i-1);
			lst=i;
		}
		tot=0;
		if(p[i]<i)l.push_back(p[i]);
		else r.push_back(p[i]);
	}
	check(l,r,lst,n);
