			flag = true;
		}
	}
	if (!flag) {
		cout << "No\n";
		return 0;
	}
	if (t % 3) {
		cout << "No\n";
	}
	else if (N % 3 && t == N * (N - 1) / 2) {
		cout << "No\n";
	}
	else {
		cout << "Yes\n";
