inline int read()
{
	int ret, f=1;
	char c;
	while((c=getchar())&&(c<'0'||c>'9'))if(c=='-')f=-1;
	ret=c-'0';
	while((c=getchar())&&(c>='0'&&c<='9'))ret=(ret<<3)+(ret<<1)+c-'0';
	return ret*f;
}

int n, a[maxn];

int s[maxn];

int lowbit(int x)
{
	return x & -x;
}

void modify(int x, int w)
{
	while(x <= n)
	{
		s[x] += w;
		x += lowbit(x);
	}
}

int query(int x)
{
	int ret = 0;
	while(x)
	{
		ret += s[x];
		x -= lowbit(x);
	}
	return ret;
}

int main()
{
