		a[i] = read();
	int s1 = 0, s2 = 0, s3 = 0, res = 0;
	for(int i = 1; i <= n; i += 2)
	{
		s1 += res - query(a[i]);
		modify(a[i], 1);
		++ res;
	}
	for(int i = 1; i <= n; i += 2)
		modify(a[i], -1);
