int N;
int A[MAX_N];

void solve() {
	cin >> N;
	rep(i, 0, N) {
		cin >> A[i];
		A[i]--;
	}
	queue<int> que;
	rep(i, 1, N - 1) {
		if(A[i] == i && A[i - 1] > A[i] && A[i] > A[i + 1]) que.push(i);
	}
	while(!que.empty()) {
		int at = que.front(); que.pop();
		swap(A[at - 1], A[at + 1]);
		for(int i = at - 2; i <= at + 2; i++) {
			if(1 <= i && i < N - 1) {
				if(A[i] == i && A[i - 1] > A[i] && A[i] > A[i + 1]) que.push(i);
			}
		}
	}
	rep(i, 0, N) {
		if(A[i] != i) {
			cout << "No\n"; return;
		}
	}
	cout << "Yes\n";
}

