void update(int x,int v){for(;x<=n;x+=x&-x)sum[x]+=v;}
int query(int x){int ret=0;for(;x>=1;x-=x&-x)ret+=sum[x];return ret;}
long long calc(int *a,int m)
{
	memset(sum,0,sizeof(sum));
	long long ans=0;
	for(int i=1;i<=m;i++)
	{
		ans+=query(n)-query(a[i]);
		update(a[i],1);
	}
	return ans;
}
