	for (int i = l;i <= r;i += 2)
		if (a[i] == i)
			return 0;
	int lst = 0;
	for (int i = l;i <= r;i += 2)
		if (a[i] < i)
		{
			if (a[i] < lst)
				return 0;
			lst = a[i];
		}
	lst = n + 1;
	for (int i = r;i >= l;i -= 2)
		if (a[i] > i)
		{
			if (a[i] > lst)
