	int x = 0, y = 0;
	for(int i = l; i <= r - 2; i += 2)
	{
		if(a[i] > i)
		{
			if(a[i] > x) x = a[i];
			else return 0;
		}
		else if(a[i] < i)
		{
			if(a[i] > y) y = a[i];
			else return 0;
		}
		else return 0;
	}
	return 1;
}

