class Add_Segment_Tree {
	vector<long long int>v;
	vector<int>l;
	vector<int>r;
	long long int ret;
	int num;
	long long int Update(int place) {
		if (place >= v.size() / 2) {
			return v[place];
		}
		v[place] = Update(place * 2) + Update(place * 2 + 1);
		return v[place];
	}
	void Sum(int a, int b, int place) {
		if (l[place] >= a && r[place] <= b) {
			ret += v[place];
			return;
		}
		if (l[place] > b || r[place] < a) return;
		Sum(a, b, place * 2);
		Sum(a, b, place * 2 + 1);
		return;
	}
public:
	void Left(int place) {
		if (place >= v.size() / 2) {
			l[place] = place - v.size() / 2;
			return;
		}
		Left(place * 2);
		Left(place * 2 + 1);
		l[place] = l[place * 2];
		return;
	}
	void Right(int place) {
		if (place >= v.size() / 2) {
			r[place] = place - v.size() / 2;
			return;
		}
		Right(place * 2);
		Right(place * 2 + 1);
		r[place] = r[place * 2 + 1];
		return;
	}
	Add_Segment_Tree(int n) {
		n++;
		num = 1;
		while (num < n * 2)num *= 2;
		l.resize(num);
		r.resize(num);
		v.resize(num, 0);
		Left(1);
		Right(1);
	}
	void Add(int place, long long int num, bool update) {
		place += v.size() / 2;
		v[place] += num;
		if (!update)return;
		place /= 2;
		while (place) {
			v[place] = v[place * 2] + v[place * 2 + 1];
			place /= 2;
		}
	}
	void TopDown() {
		Update(1);
	}
	long long int Sum(int a, int b) {
		ret = 0;
		Sum(a, b, 1);
		return ret;
	}
};

int main() {
	ios::sync_with_stdio(false);
	cin.tie(0);

	cin >> N;
	vector<int>v(N + 1);
	Add_Segment_Tree asg(N);
	long long int t = 0;
	for (int i = 1; i <= N; i++) {
		cin >> v[i];
		asg.Add(v[i], 1, true);
		t += asg.Sum(v[i] + 1, N);
		if (v[i] % 2 != i % 2) {
			cout << "No\n";
			return 0;
		}
	}
	bool flag = false;
	bool cleared = true;
	for (int i = 3; i <= N; i++) {
		if (v[i] < v[i - 1] && v[i - 1] < v[i - 2]) {
			flag = true;
		}
		if (!(v[i - 2] < v[i - 1] && v[i - 1] < v[i])) {
			cleared = false;
		}
	}
	if (cleared) {
		cout << "Yes\n";
		return 0;
	}
	if (!flag) {
		cout << "No\n";
		return 0;
	}
	if (t % 3) {
		cout << "No\n";
	}
	else if (N % 3 && t == N * (N - 1) / 2) {
		cout << "No\n";
	}
	else {
		cout << "Yes\n";
	}
	return 0;
}

