int N;
int A[300001];

int cnt_bubble(int b,int e){
  if(b==e)return 0;
  if(b+2==e)return A[b]>A[e]?1:0;
  int mid=(b+e)/2;
  if((mid-e)%2==1)mid--;
  int ret=0;
  int l=cnt_bubble(b,mid);
  int r=cnt_bubble(mid+2,e);
  ret+=l+r;
  int ln=(mid-b)/2+1,rn=(e-(mid+2))/2+1;
  int bb=b,mm=mid+2;
  int* tmp=(int *)malloc((ln+rn)*sizeof(int));
  int i=0;
  while(bb<=mid&&mm<=e){
    if(bb>mid||mm>e){
      if(bb>mid){
        for (int ii = i; ii < (ln+rn); ii++) {
          tmp[ii]=A[mm];
          mm+=2;
        }
      }else{
        for (int ii = i; ii < (ln+rn); ii++) {
          tmp[ii]=A[bb];
          bb+=2;
        }
      }
      break;
    }
    if(A[bb]>A[mm]){
      tmp[i]=A[mm];
      i++;
      ret+=ln;
      mm+=2;
    }
    else{
      tmp[i]=A[bb];
      i++;
      bb+=2;
    }
  }
  for (int i = 0; i < (ln+rn); i++) {
    A[e+(i*2)]=tmp[i];
  }
  return 0;
}

int main(){
  cin>>N;
  REP(N){
    cin>>A[i];
    A[i]--;
    if((A[i]%2)!=(i%2)){
      cout<<"No"<<endl;
      return 0;
    }
  }
  int i=0;
  while(i<N){
    int b=i,e=A[i];
    int j=i;
    while(true){
      e=max(e,A[j]);
      if(j>e)goto NO;
      if(j==e)break;
      j++;
    }
    if((e-b)%2==1)
      goto NO;
    if(b==e){
      i=e+1;
      continue;
    }
    int cnt=0;
    for (int j = b+1; j < e; j+=2) {
      if(A[j]!=j)
        goto NO;
      if(A[j-1]>A[j]&&A[j]>A[j+1])cnt++;
    }
    if(cnt!=1)goto NO;
    cnt=cnt_bubble(b,e);
    if(cnt!=(e-b)/2)
      goto NO;
    i=e+1;
  }
  cout<<"Yes"<<endl;
  return 0;
NO:
  cout<<"No"<<endl;
  return 0;
}

