        int L = pos[R] - offs * 2;
        //printf("R = %d offs = %d L = %d\n", R, offs, L);
        if ((L % 2) != (R % 2)) NO();
        if (offs && L != R) NO();
        if (L == R) continue;
        update(pos[R], 1, N);

        for (int i = L + 1; i < R; i += 2) {
            if (color[i]) break;
            color[i] = 1;
        }
    }
    puts("Yes");
    return 0;
}

