		for(k=l;k<=r;++k)
			if(a[k]<l||a[k]>r)
				return puts("No"),0;
		for(k=l+1;k<=r;k+=2)
			if(a[k]!=k)
				return puts("No"),0;
		lstl=lstr=0;
		for(k=l;k<=r;++k)
			if(a[k]!=k){
