bool check(int l,int r) {
	for(int i=l;i<=r;++i) {
		if(p[i]<l||p[i]>r) {
			return false;
		}
	}
	int L=-1,R=-1;
	for(int i=l;i<=r;i+=2) {
		if(p[i]<i) {
			if(L>p[i]) return false;
			L=p[i];
		}
		else {
			if(R>p[i]) return false;
			R=p[i];
		}
	}
	return true;
} 
bool sol() {
