		while(l<=N&&p[l]==l) ++l;
		if(l>N) break;
		int r=l;
		while(r+2<=N&&p[r+1]==r+1&&p[r+2]!=r+2) r+=2;
		if(!check(l,r)) return false;
		l=r+1;
	}
	return true;
}
