struct Adder{
	Adder(){a = b = 0;}
	Adder(int a_, int b_){a = a_; b = b_;}
	int a,b;
	Adder operator +(Adder t){return {a+t.a,b+t.b};}
}B[300300];

int N,A[300300];

void push(int x, Adder t)
{
	while (x <= N){
		B[x] = B[x] + t;
		x += x & (-x);
	}
}

Adder pop(int x)
{
	Adder t;
	while (x){
		t = t + B[x];
		x -= x & (-x);
	}
	return t;
}

