inline bool chk(int l, int r) {
  int le = 0, ri = 0;
  for (int i = l; i <= r; ++ i) {
    if(a[i] < l || a[i] > r) return false;
    if(a[i] < i) {
      if(a[i] < le) return false;
      le = a[i];
    }
    if(a[i] > i) {
      if(a[i] < ri) return false;
      ri = a[i];
    }
  }
  return true;
}

int main() {
