using namespace std;

bool func(vector<int> &p, int s){
    int mx = p[s];
    int mn = p[s+1];
    int tmn = s-1;
    for(int t=s-2; t>0; t-=2){
        if(mn < p[t] && p[t] < mx){
        }else if(p[t] < mn){
            return t+1;
        }
        else return -1;

        if(p[t] < p[t-1] && p[t-1] < mx){
            mx = p[t];
        }else return -1;
        tmn = t-1;
    }
    return tmn;
}

int main(){
    int N;
    cin >> N;
    vector<int> p(N);
    for(int i=0; i<N; i++) cin >> p[i];

    vector<int> p_rev(N);
    for(int i=0; i<N; i++)
        p_rev[i] = -p[N-1-i];

    for(int i=1; i<N-1; i++){
        if(p[i-1] > p[i] && p[i] > p[i+1]){
            int l = func(p, i);
            int r = func(p_rev, N-1-i);
            r = N-1-r;

            if(l == -1 || r == -1){
                cout << "No" << endl;
                return 0;
            }
            sort(p.begin()+l, p.begin()+r+1);
        }
    }

    for(int i=0; i<N-1; i++)
        if(p[i] > p[i+1]){
            cout << "No" << endl;
            return 0;
        }
    cout << "Yes" << endl;
    return 0;
}

