using namespace std;
int a[303030];
void jud(int l,int r)
{
	int now1=0,now2=0;
	for(int i=l;i<=r;i+=2)
	{
		if(i<a[i])
		{
			if(a[i]>now1)
				now1=a[i];
			else
			{
				printf("No");
				exit(0);
			}	
		}
		else
		{
			if(a[i]>now2)
				now2=a[i];
			else
			{
				printf("No");
				exit(0);
			}
		}
		if(a[i]<l||a[i]>r)
		{
			printf("No");
			exit(0);
		}
	}
}
int main()
{
	int n,l=0;
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
		scanf("%d",&a[i]);
	a[n+1]=n+1;
	a[n+2]=n+2;
	n+=2;
	for(int i=1;i<=n;i++)
	{
		if(a[i]!=i)
		{
			if(l==0)
				l=i;
			else if((i-l)&1)
			{
				jud(l,i-1);
				l=i;
			}
		}
		else
		{
			if(l&&((i-l)&1)==0)
			{
				jud(l,i-2);
				l=0;
			}
		}
	}
	printf("Yes");
	return 0;
}

#include<cstdio>
#include<algorithm>
#define MN 300005
using namespace std;
int n,a[MN];
bool check(int l,int r){
	int maxx=0,minx=n;
	for(int i=l;i<=r;i++)maxx=max(maxx,a[i]),minx=min(minx,a[i]);
	if(minx!=l||maxx!=r)return 0;int tmpx=0,tmpy=0;
	for(int i=l;i<=r;i+=2){
		if(a[i]>tmpx)tmpy=tmpx,tmpx=a[i];
		else if(a[i]>tmpy)tmpy=a[i];
		else return 0;
	}return 1;
}
int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++)scanf("%d",&a[i]);
	for(int i=1;i<=n;i++)if((a[i]&1)^(i&1))return 0*puts("No");
	for(int i=1,j;i<=n;i=j+1){
		j=i;
		if(a[i]!=i){
			while(1){
				if(j+2>n||a[j+1]!=j+1||a[j+2]==j+2)break;
				j+=2;
			}
			if(!check(i,j))return 0*puts("No");
		}	
	}puts("Yes");
}

