using namespace std;
inline int read(){
	int ans=0;
	char ch=getchar();
	while(!isdigit(ch))ch=getchar();
	while(isdigit(ch))ans=(ans<<3)+(ans<<1)+(ch^48),ch=getchar();
	return ans;
}
inline void write(int x){
	if(x>9)write(x/10);
	putchar(x%10^48);
}
int n,a[N],pos[N];
int main(){
	n=read();
	for(int i=1;i<=n;++i)a[i]=read(),pos[a[i]]=i;
	for(int i=1;i<=n;++i)if((a[i]&1)!=(i&1)){cout<<"No";return 0;}
	for(int i=n;i>=1;--i){
		if(pos[i]==i)continue;
		int&j=pos[i];
		while(j!=i){
			if(a[j+1]<a[j+2])break;
			else swap(a[j],a[j+2]),swap(pos[a[j]],pos[a[j+2]]);
		}
		if(j!=i){cout<<"No";return 0;}
	}
	cout<<"Yes";
	return 0;
}

