int main() {
    int N;
    scanf("%d", &N);
    for (int i = 1; i <= N; i++) {
        scanf("%d", &p[i]);
        pos[p[i]] = i;
    }
    for (int i = N; i >= 1; i--) {
        int cur = pos[i];
        if ((cur % 2) != (i % 2)) NO();
        while (cur <= N && cur != i) {
            if (p[cur + 1] < p[cur + 2] || visit[p[cur + 1]]) break;
            int f1 = i, f2 = p[cur + 2];
            swap(p[cur], p[cur + 2]);
            visit[p[cur + 1]] = visit[p[cur + 2]] = 1;
            pos[f1] = cur + 2;
            pos[f2] = cur;
            cur += 2;
        }
        visit[i] = 1;
        //printf("i = %d cur = %d\n", i, cur);
        if (cur != i) NO();
    }
    puts("Yes");
    return 0;
}

