vector<int> tor, tol;

bool fun(int a, int b)
{
	if ((b - a) % 2) return false;
	for (int i = a + 1; i < b; i += 2) {
		if (p[i] != i) return false;
	}
	tor.clear(); tol.clear();
	for (int i = a; i <= b; i += 2) {
		if (p[i] > i) tor.pb(p[i]);
		else tol.pb(p[i]);
	}
	for (int i = 1; i < (int)tor.size(); i++) {
		if (tor[i] < tor[i - 1]) return false;
	}
	for (int i = 1; i < (int)tol.size(); i++) {
		if (tol[i] < tol[i - 1]) return false;
	}
	return true;
}

