	for (int i = 1; i <= n; i++) {
		mx = max(mx, p[i]);
		if (mx == i) {
			if (!fun(from, i)) {
				cout << "No" << E;
				return 0;
			}
			from = i + 1;
		}
	}
	cout << "Yes" << E;
