using namespace std;
const int N = 3e5 + 5;
int n, a[N];
bool go() {
	bool done = true;
	for (int i = 1; i <= n; ++i)
		if (a[i] != i)
			done = false;
	if (done)
		return true;
	for (int i = 2; i < n; ++i) {
		if (a[i - 1] > a[i] && a[i] > a[i + 1]) {
			swap(a[i - 1], a[i + 1]);
			if (go()) {
				return true;
			}
			swap(a[i - 1], a[i + 1]);
		}
	}
	return false;
}
int idx[N];
int main() {
#ifndef ONLINE_JUDGE
//	freopen("input.in", "r", stdin);
#endif
	scanf("%d", &n);
	for (int i = 1; i <= n; ++i) {
		scanf("%d", a + i);
		if (a[i] % 2 != i % 2) {
			puts("No");
			return 0;
		}
		idx[a[i]] = i;
	}
	for (int i = 1; i <= n; ++i) {
		int j = idx[i];
		while (j > 2 && a[j - 2] > a[j - 1] && a[j - 1] > a[j] && a[i] != i) {
			swap(idx[a[j - 2]], idx[a[j]]);
			swap(a[j - 2], a[j]);
			j = idx[i];
		}
		if (a[i] != i) {
			puts("No");
			return 0;
		}
	}
	puts("Yes");
//	if (go())
//		puts("Yes");
//	else
//		puts("No");
	return 0;
}

