struct BIT {
	int c[N],res;
	inline int lowbit(int x) {return x&(-x);}
	inline void update(int x) {for(;x<=n;x+=lowbit(x)) ++c[x];}
	inline int query(int x) {res=0;for(;x;x-=lowbit(x)) res+=c[x];return res;}
}bit;

inline ll calc(int*a,int len) {
	memset(bit.c,0,sizeof(bit.c));
	ll ans=0;
	for(int i=len;i;--i) ans+=bit.query(a[i]-1),bit.update(a[i]);
	return ans;
}

