            return 0;
        }
    }
    int mx[2] = {0, 0};
    for (int i = 0, j = 0; i < n; ++i) {
        if (q[i] >= j) {
            mx[i % 2] = max(mx[i % 2], i);
            for (int k = j; k < q[i]; ++k) {
                if (p[k] < mx[k % 2]) {
                    cout << "No" << endl;
                    return 0;
                }
                mx[k % 2] = p[k];
                if (k % 2 != i % 2 && p[k] != k) {
                    cout << "No" << endl;
                    return 0;
                }
            }
            p[q[i]] = mx[i % 2];
            j = q[i];
        }
    }
    cout << "Yes" << endl;
    return 0;
}

