#include <bits/stdc++.h>

template <class T = long long int>
class Bit {
    private:
    T len;
    T *arr;

    public:
    Bit(T l, T initialValue = 0) : len(l) {
        arr = new T[l+1];
        for (T i = 0; i < l + 1; i++) {
            arr[i] = initialValue;
        }
    }
    ~Bit() {
        delete[] arr;
    }

    void update(T a, T newval) {
        for (T x = a; x <= len; x += x & -x) arr[x] += newval;
    }
    T query(T a) const {
        T ret = 0;
        for (T x = a; x > 0; x -= x & -x) ret += arr[x];
        return ret;
    }
};

int main() {
    int n;
    std::cin >> n;
    std::vector<int> p(n + 1);
    std::deque<std::deque<int>> qs {};
    for (int i = 1; i <= n; i++) {
        std::cin >> p[i];
        if (p[i] % 2 != i % 2) {
            std::cout << "No" << std::endl;
            return 0;
        }
    }

    int i = 1;
    while (i < n) {
        while (i <= n - 1 && p[i] == i) i++;
        qs.push_back({p[i++]});
        while (i <= n - 1 && p[i] == i) {
            qs.back().push_back(p[i + 1]);
            i += 2;
        }
    }

    for (auto& q : qs) {
        if (!q.size()) continue;

        Bit<int> b(1000000);
        int inv = 0;
        for (auto el : q) {
            inv += b.query(el);
            b.update(el, 1);
        }
        inv = q.size() * (q.size() - 1) / 2 - inv;
        if (inv > q.size() - 1) {
            std::cout << "No" << std::endl;
            return 0;
        }
    }

    std::cout << "Yes" << std::endl;

    return 0;
}

