using namespace std;

bool valid(vector<int> &seg, vector<int> &cor) {
	int n = (int) seg.size();
	auto ss = seg;
	auto sc = cor;
	sort(ss.begin(), ss.end());
	sort(sc.begin(), sc.end());
	if (ss != sc) {
		return false;
	}
	map<int, int> pos;
	rep(i, n) {
		pos[cor[i]] = i;
	}
	vector<int> l, r;
	rep(i, n) {
		if (i < pos[seg[i]]) {
			l.push_back(seg[i]);
		} else if (i > pos[seg[i]]) {
			r.push_back(seg[i]);
		} else {
			assert(false);
		}
	}
	rep(i, (int) l.size() - 1) {
		if (l[i] > l[i + 1]) {
			return false;
		}
	}
	rep(i, (int) r.size() - 1) {
		if (r[i] > r[i + 1]) {
			return false;
		}
	}
	return true;
}

int main() {
	int n;
	scanf("%d", &n);
	vector<int> p(n);
	vector<bool> same(n);
	rep(i, n) {
		scanf("%d", &p[i]);
		p[i] --;
		same[i] = (p[i] == i);
	}
	vector<int> seg;
	vector<vector<int>> segs;
	vector<int> cor;
	vector<vector<int>> cors;
	rep(i, n) {
		if (!same[i]) {
			if (i == 0) {
				if (!same[i + 1]) {
					puts("No");
					return 0;
				} else {
					seg.push_back(p[i]);
					cor.push_back(i);
				}
			} else if (same[i - 1]) {
				seg.push_back(p[i]);
				cor.push_back(i);
			} else if (i == n - 1) {
				if (!same[i - 1]) {
					puts("No");
					return 0;
				}
			} else if (same[i + 1]) {
				segs.push_back(seg);
				cors.push_back(cor);
				seg.clear();
				cor.clear();
				seg.push_back(p[i]);
				cor.push_back(i);
			} else {
				puts("No");
				return 0;
			}
		}
	}
	if (seg.size() > 0) segs.push_back(seg);
	if (cor.size() > 0) cors.push_back(cor);
	rep(i, segs.size()) {
		if (!valid(segs[i], cors[i])) {
			puts("No");
			return 0;
		}
	}
	puts("Yes");
        return 0;
}

