void solve(ll N, vector<ll> p) {
  SegmentTree<RMQ_with_index<int>> seg(N);
  REP(i,N) seg.update(i, pair<int,int>(p[i] - 1, i));
  REP(i,N) {
    int lv, rv, pos;
    tie(rv, pos) = seg.query(i, N);
    if (pos == i) continue;
    if (pos == i + 1) {
      cout << "No" << endl;
      return;
    }
    lv = seg.query(i, i + 1).first;
    seg.update(pos, std::make_pair(lv, pos));
    seg.update(i, std::make_pair(rv, i));
  }
  cout << "Yes" << endl;
}

