int n, x;
struct seq {
	int s[N], ans, tot, v[N];
	seq() { ans = tot = 0; memset(s, 0, sizeof s); }
	inline int lowbit(int x) { return x & -x; }
	inline void insert(int x) { for (; x <= N; x += lowbit(x)) ++s[x]; }
	inline int query(int x, int ret = 0) {for (; x; x -= lowbit(x)) ret += s[x]; return ret; }
	inline void add(int x) { v[++tot] = x; }
	inline void calc() { ans = tot * (tot - 1) / 2; for (int i = 1; i <= tot; ++i) ans -= query(v[i] - 1), insert(v[i]); }
} P, Q, R;

